def plot_data(ax,data_set):
    mv = data_set[:]
    pt = mv[0:len(mv):int((len(mv) / 7)) + 1]
    if pt[-1] != mv[-1]:
        pt.append(mv[-1])
    d = list(range(len(mv)))
    ptx = d[0:len(d):int((len(d) / 7)) + 1]
    if ptx[-1] != d[-1]:
        ptx.append(d[-1])
    a = min([len(ptx), len(pt)])
    ax.plot(ptx[:a], pt[:a], linestyle=(0, (3, 1, 1, 1, 1, 1)),
            linewidth=2, **{'color': 'b', 'marker': 's'})
    ax.set_ylabel("network cost")
    ax.set_xlabel('Time (seconds)')
    ax.set_title('network cost over Time')
    plt.subplot(ax)

